mapper.select:
select id
     , name
from user
where id = #{id}
  AND password = #{password};

SELECT COUNT(DISTINCT name)
FROM user
WHERE id = #{id}

mapper.update:
UPDATE auctionList
   SET
       nowPrice = #{money},
       buyerId = #{id}
WHERE
   no = #{no}

UPDATE auctionList
   SET
       status = #{status}
WHERE
   no = #{no}

UPDATE user
   SET
       amount = amount - #{price}
WHERE
   id = #{id}
